mapper.select:
SELECT shop_id, owner_id, area_id, shop_category_id, parent_category_id, shop_name, shop_desc, shop_addr, phone, shop_img, longitude, latitude, priority, create_time, last_edit_time, enable_status, advice FROM tb_shop
and shop_id = #{shopCondition.shopId}
and shop_category_id = #{shopCondition.shopCategory.shopCategoryId}
and parent_category_id = #{shopCondition.parentCategory.shopCategoryId}
and area_id = #{shopCondition.area.areaId}
and shop_name like '%${shopCondition.shopName}%'
and enable_status = #{shopCondition.enableStatus}
ORDER BY priority DESC LIMIT #{rowIndex},#{pageSize};

SELECT
count(1)
FROM
tb_shop


		and shop_id = #{shopCondition.shopId}


		and shop_category_id = #{shopCondition.shopCategory.shopCategoryId}


		and parent_category_id = #{shopCondition.parentCategory.shopCategoryId}


		and area_id = #{shopCondition.area.areaId}



		and shop_name like '%${shopCondition.shopName}%'


		and enable_status = #{shopCondition.enableStatus}

mapper.update:
update tb_shop
shop_name=#{shopName}, shop_desc=#{shopDesc}, shop_addr=#{shopAddr}, phone=#{phone}, shop_img=#{shopImg}, longitude=#{longitude}, latitude=#{latitude}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, advice=#{advice}, area_id=#{area.areaId}, shop_category_id=#{shopCategory.shopCategoryId}, parent_category_id=#{parentCategory.shopCategoryId}
where shop_id=#{shopId}
