mapper.select:
SELECT user_shop_id, user_id, shop_id, user_name, shop_name, create_time, point FROM tb_user_shop_map
and user_id = #{userShopCondition.userId}
and shop_id = #{userShopCondition.shopId}
and user_name like '%${userShopCondition.userName}%'
and shop_name like '%${userShopCondition.shopName}%'
and create_time > #{userShopCondition.createTime}
LIMIT #{rowIndex},#{pageSize};

SELECT
count(1)
FROM
tb_user_shop_map


		and user_id = #{userShopCondition.userId}


		and shop_id = #{userShopCondition.shopId}



		and user_name like '%${userShopCondition.userName}%'


		and shop_name like '%${userShopCondition.shopName}%'


		and create_time > #{userShopCondition.createTime}

mapper.update:
UPDATE tb_user_shop_map
SET point=#{point}
where user_id = #{userId}
AND shop_id = #{shopId}
